import { useState } from 'react'

const App = () => {
  const [persons, setPersons] = useState([
    { name: 'Arto Hellas', number: '040-123456' },
    { name: 'Ada Lovelace', number: '39-44-5323523' },
    { name: 'Dan Abramov', number: '12-43-234345' },
    { name: 'Mary Poppendieck', number: '39-23-6423122' }
  ]) 
  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')
  const [newFilter, setNewFilter] = useState('')
  const [namesToShow, setNamesToShow] = useState(persons)

  const addName = (event) => {
    event.preventDefault()
    const name = {
      name: newName,
      number: newNumber
    }
    persons.map(name => name.name).includes(newName)
      ? window.alert(`${newName} has already been added to phonebook`)
      : setPersons(persons.concat(name))
  }

  const handleFiltering = (event) => {
    setNewFilter(event.target.value.toLowerCase())
    {console.log(namesToShow)}
    {console.log("filter:", newFilter)}
    if(newFilter == "") {
      setNamesToShow(persons)
      console.log("resetting filter")
    } else {
      setNamesToShow(persons.filter(name => name.name.toLowerCase().includes(newFilter)))
    }
  }

  return (
    <div>
      <h2>Phonebook</h2>
      filter shown with <input
                        onChange={handleFiltering}
                        value={newFilter}
                        />
      <h2>add a new</h2>
      <form onSubmit={addName}>
        <div>
          name: <input 
                value={newName}
                onChange={(event) => setNewName(event.target.value)}
                />
        </div>
        <div>
          number: <input
                  value={newNumber}
                  onChange={(event) => setNewNumber(event.target.value)}
                  />
        </div>
        <div>
          <button type="submit">add</button>
        </div>
      </form>
      <h2>Numbers</h2>
      {namesToShow.map(name => <p key={name.name}>{name.name} {name.number}</p>)}
    </div>
  )

}

export default App
