import { useState } from 'react'
import Names from './components/names'
import Filter from './components/filter'
import AddNew from './components/addNew'

const App = () => {
  const [persons, setPersons] = useState([
    { name: 'Arto Hellas', number: '040-123456' },
    { name: 'Ada Lovelace', number: '39-44-5323523' },
    { name: 'Dan Abramov', number: '12-43-234345' },
    { name: 'Mary Poppendieck', number: '39-23-6423122' }
  ]) 
  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')
  const [newFilter, setNewFilter] = useState('')
  const [namesToShow, setNamesToShow] = useState(persons)

  const addName = (event) => {
    event.preventDefault()
    const name = {
      name: newName,
      number: newNumber
    }
    persons.map(name => name.name).includes(newName)
      ? window.alert(`${newName} has already been added to phonebook`)
      : setPersons(persons.concat(name))
  }

  const handleFiltering = (event) => {
    setNewFilter(event.target.value.toLowerCase())
    if(event.target.value == "") {
      setNamesToShow(persons)
    } else {
      setNamesToShow(persons.filter(name => name.name.toLowerCase()
                    .match(event.target.value)))
    }
  }

  const handleNewName = (event) => {setNewName(event.target.value)}
  const handleNewNumber = (event) => {setNewNumber(event.target.value)}

  return (
    <div>
      <h2>Phonebook</h2>
      <Filter newFilter={newFilter} handleFiltering={handleFiltering} />
      <h2>add a new</h2>
      <AddNew addName={addName} newName={newName} handleNewName={handleNewName} newNumber={newNumber} handleNewNumber={handleNewNumber} />
      <Names names={namesToShow} />
    </div>
  )

}

export default App
